

public class Manipulación_Cadenas_3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String alumno1, alumno2;
		
		alumno1="David";
		
		alumno2="david";
		
		System.out.println(alumno1.equals(alumno2));//Distingue mayúsculas y minúsculas
		
		System.out.println(alumno1.equalsIgnoreCase(alumno2));//No distingue mayúsculas y minúsculas
		
		

	}

}
